INSERT INTO games (id, name, summary, release) VALUES (18526, 'The Sorcerer of Claymorgue Castle', 'You are the sorcerer apprentice of Solon and have to retrieve thirteen Stars of Power for you master from Castle Claymorgue.', '1984-12-30') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (17248, 'The Sopranos: Road to Respect', 'The Sopranos: Road to Respect is a 2006 video game based on the HBO series The Sopranos, which aired from January 10, 1999 to June 10, 2007. It was released on November 7, 2006.
INSERT INTO games (id, name, summary, release) VALUES (26529, 'The Song of Seven : Overture (Chapter One)', 'Take a boy and a girl on a journey of enlightenment.', '2016-05-16') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (11221, 'The Solus Project', 'A single player exploration and survival experience, played in first person and developed in Unreal Engine 4. Set on a mysterious and uninhabited alien planet.
