INSERT INTO games (id, name, summary, release) VALUES (2844, 'The Simpsons: Hit & Run', 'The Simpsons: Hit &amp; Run is a GTA inspired, adventure game. Putting you in the shoes of: Homer, Bart, Lisa, Marge, and Apu, you are set to save Springfield from the aliens Kang and Kodos.', '2003-09-15') ON CONFLICT DO NOTHING;
