INSERT INTO games (id, name, summary, release) VALUES (11625, 'The Room', 'The Room is a 2012 puzzle video game developed by British-based Fireproof Games. The game was originally developed for the iOS platform and released in September 2012. The Android version debuted as part of a Humble Bundle in March 2013 and was subsequently released on Google Play.
