INSERT INTO games (id, name, summary, release) VALUES (10733, 'The Novelist', 'The Novelist asks one central question: can you achieve your dreams without pushing away the people you love? The game focuses on Dan Kaplan, a novelist struggling to write the most important book of his career while trying to be the best husband and father he can be.', '2013-12-09') ON CONFLICT DO NOTHING;
