INSERT INTO games (id, name, summary, release) VALUES (90, 'The Need for Speed', 'Welcome to The Need For Speed racing game. Here you''ll find spine-tingling action coupled with awesome graphics. As you race down an alpine road in your choice of eight high performance cars, you''ll be amazed by the detail and realism. The sophisticated physics model recreates the feel of the cars themselves; you''ll notice the difference in handling, breaking and just plain power. Plan your strategy for each track, there are six to choose from during regular play, and each demands a different style of driving. 
INSERT INTO games (id, name, summary, release) VALUES (25432, 'The Nations Gold', null, null) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (9172, 'The Nations', 'The Nations is a real-time strategy game for Windows based on building a nation and conquering others. The player may choose from 3 races – the Pimmons, the Amazons and the Sajikis. Select a tribe and build up a harmonious community, rulling over them honestly and fairly, discovering new lands and skills, fighting for survival and power. You are now in control of the lives of tribes within The Nations. Take into account that everyone is an individual with their very own habits and characteristics that need to be nurutred and developed.', '2001-12-30') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (5046, 'The Naked Brothers Band', null, '2008-10-19') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (25659, 'The NES Classic Edition', null, null) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (12449, 'The Mystery of the Druids', null, '2000-12-30') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (16811, 'The Mysterious Cities of Gold', 'Play Esteban, Zia and Tao through numerous tricky levels full of puzzles, traps and infiltration phases in a wide variety of diverse gorgeous settings. Explore every nook and cranny of China, and escape Zarès’ hostile men, searching for the local City of Gold. 
INSERT INTO games (id, name, summary, release) VALUES (5024, 'The Munchables', 'The player controls creatures of insatiable appetite, using their regurgitating prowess to consume an army of invading Tabemon Space Pirates, each of whom bears an uncanny resemblance to various foods.', '2009-05-25') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (5023, 'The Mummy: Tomb of the Dragon Emperor', null, '2008-07-21') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (3023, 'The Movies: Stunts and Effects', 'The Movies: Stunts and Effect is the only expansion to The Movies game.', '2006-06-05') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (2995, 'The Movies', 'Behind every box office hit and Hollywood star is a movie mogul who makes it happen. In The Movies, that mogul is you. Create your own studio from the ground up. Direct your own movies and call the shots on budgets, actors, locations and more. From script to screen, will your films be smash hits or break the bank? It''s all up to you, baby.', '2005-11-07') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (26409, 'The Movie Monster Game', null, '1986-12-30') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (17965, 'The Moon Sliver', 'The Moon Sliver is a short narrative-focused non-linear exploration game, with elements of horror.  It features a unique narrative mechanic that blurs the line between story and exploration, where interacting with objects and even simply moving around will reveal fragments of narration.  As you piece these fragments together, a story of guilt, loneliness, and faith begins to take shape.  And it becomes increasingly obvious that something is hunting you...', '2014-09-01') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (3162, 'The Monkey Wrench Conspiracy', 'The Monkey Wrench Conspiracy &quot;mod&quot; puts you in the role of an intergalactic secret agent dispatched to deep space to rescue the Copernicus station from alien hijackers. It is a complete tutorial for a complex technical product, designed to teach industrial engineers how to use new 3-D design software.', '1999-12-29') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (18171, 'The Moment of Silence', 'The Moment of Silence is a classic point-and-click 3rd person adventure game set in New York City in 2044. Players step into the role of Peter Wright, an advertising executive currently heading up the Government''s ''Freedom of Speech'' campaign.', '2004-11-04') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (26020, 'The Mist', null, '1985-12-30') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (24447, 'The Missing Ring', null, '1982-12-30') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (25116, 'The Miser''s House', 'This game was first released as MISER for the Commodore PET in 1981. Considering the strict constraints of the early 8-bit machines — the original version only required 16K of memory to run — it was remarkable for its complexity.', '1981-12-30') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (1753, 'The Misadventures of Tron Bonne', null, '1999-07-21') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (13179, 'The Misadventures of P.B. Winterbottom', 'Enter a macabre and comical silent world filled with mischief, time travel and delicious pie. Includes Steam Achievements, Cloud, and Leaderboards!', '2010-02-16') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (24044, 'The Minims', 'The Minims - A New Beginning is an adventure game in the vein of point and click games, about a personal journey of an endearing creature of the Minim species. 
INSERT INTO games (id, name, summary, release) VALUES (13739, 'The Mine', null, null) ON CONFLICT DO NOTHING;
