INSERT INTO games (id, name, summary, release) VALUES (10973, 'The Journey Down: Chapter Two', 'Far below the noisy metropolis of St. Armando, Bwana follows in the footsteps of his lost father together with Kito and Lina. In their search for the mysterious Underland, they discover a dark conspiracy shrouding the fate of Captain Kaonandodo.
INSERT INTO games (id, name, summary, release) VALUES (15911, 'The Journey Down: Chapter Three', 'Chapter 3 is now being developed by SkyGoblin.', null) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (9048, 'The Journey Down: Chapter One', 'In a shady corner of Kingsport Bay at the outskirts of St Armando, Bwana and his trusted sidekick Kito struggle to make ends meet at their run-down gas station. Little do they know that they are about to be thrown into a spine-tingling adventure that will take them far from home and right into a twisting plot of corruption and danger.
INSERT INTO games (id, name, summary, release) VALUES (19082, 'The Jackbox Party Pack 3', 'The threequel to the party game phenomenon features the deadly quiz show Trivia Murder Party, the say-anything sequel Quiplash 2, the surprising survey game Guesspionage, the t-shirt slugfest Tee K.O., and the sneaky trickster game Fakin’ It.', '2016-10-17') ON CONFLICT DO NOTHING;
