INSERT INTO games (id, name, summary, release) VALUES (7144, 'The Inner World', 'The Inner World is a classic-style hand-drawn Adventure game for PC &amp; Mac. Solve plenty of challenging puzzles with Robert and Laura in the twisted world of Asposia. Experience an atmospheric and humorous story full of bizarre characters.', '2013-07-17') ON CONFLICT DO NOTHING;
