INSERT INTO games (id, name, summary, release) VALUES (20945, 'The Evil Within: The Consequence', 'The Consequence is the second DLC for The Evil Within, as well as the second and final part of the two-part DLC, the first being The Assignment. It revolves around Juli Kidman, continuing the plot from The Assignment as well as revealing more from Juli''s past. This DLC will also allow Juli to defend herself more. It was released on April 21st 2015.', null) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (9183, 'The Evil Within: The Assignment', null, null) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (4786, 'The Evil Within', 'Shinji Mikami, the father of survival horror, is back to direct The Evil Within - a game embodying the meaning of pure survival horror. Highly-crafted environments, horrifying anxiety, and an intricate story weave together to create an immersive world that will bring players to the height of tension.', '2014-10-13') ON CONFLICT DO NOTHING;
