INSERT INTO games (id, name, summary, release) VALUES (11744, 'The Escapists: The Walking Dead', 'Manage your escape from an onslaught of walkers in this cross-over between the time management escape game The Escapists and the blockbuster zombie franchise The Walking Dead. Using the retro graphical style of The Escapists, the game gives you control of Rick Grimes as he tries to secure the safety of a number of familiar characters by seeking out a safe escape route from each area, fending off the zombie hordes and keeping as many of them alive as possible as they escape. The game will faithfully keep to the timeline of the comics, so as you progress you''ll move through a series of familiar locations.', '2015-09-29') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (25653, 'The Escapists 2', null, null) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (9241, 'The Escapists', 'All that stands between you and your freedom is a perfectly executed escape plan. The Escapists provides players the opportunity of experiencing a light-hearted insight into everyday prison life with the main objective being that of breaking out.', '2015-02-12') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (10794, 'The Escapist', 'A base of 2D platformer with extra spicy bouncing, in a mix of 48 heavily trapped levels, sided with 3 badass bosses. There''s also tasty upgrades to avoid choking. Best served hardcore, enjoy!', '2014-09-11') ON CONFLICT DO NOTHING;
