INSERT INTO games (id, name, summary, release) VALUES (17952, 'The Deer God', 'The Deer God is a breathtaking 3D pixel art adventure that will challenge your religion and your platforming skills.  It''s a game about survival, reincarnation, and karma; all set in a breathtaking and unique 3D pixelized world. Feast your eyes on the beautiful lighting, day and night system, and vast landscapes.', '2015-01-04') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (15324, 'The Deer', '"The Deer" is an educational inter­ac­tive expe­ri­ence for children. You''ll be an ancient Deer during the Pleistocene (Ice Age) epoch and you''ll discover everything about this marvelous era. Unfortunately there were no T-Rex during this age since they were already extinct millions of years before.', '2015-12-01') ON CONFLICT DO NOTHING;
