INSERT INTO games (id, name, summary, release) VALUES (26467, 'The Deed', 'A keenly dark twist on the traditional murder mystery setting, The Deed is an Adventure/RPG game about getting away with murder. Arran Bruce, the rightful heir to Dunshiel House, returns home to settle a score with his estranged family.', null) ON CONFLICT DO NOTHING;
