INSERT INTO games (id, name, summary, release) VALUES (62, 'The Curse of Monkey Island', 'Select from a variety of buccaneer hairstyles at The Barbery Coast. Enjoy a warm reunion with an old flame. Piracy never looked so good!
INSERT INTO games (id, name, summary, release) VALUES (25592, 'The Curse of Crowley Manor', null, '1981-12-30') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (19319, 'The Curious Expedition', 'Curious Expedition is a roguelike expedition simulation set in the late 19th century. Together with famous personalities you will venture on unprecedented expeditions to regions never explored before for fame, science and treasures.', '2016-09-01') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (19344, 'The Curio Society: Eclipse Over Mesina', '"After you''re contacted by a mysterious secret organization called The Curio Society, you immediately head out into the field to prove yourself. But you soon realize you might be in a little over your head – an entire town has been overrun by living statues, controlled by an ancient sorcerer bent on power and destruction. Hey, no one said job interviews were easy, but if anyone''s up to the task, it''s you! Prove yourself worthy to join the Curio Society in this thrilling Hidden-Object Puzzle Adventure game!"', null) ON CONFLICT DO NOTHING;
