INSERT INTO games (id, name, summary, release) VALUES (13526, 'The Covenant', null, null) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (17435, 'The Counting Kingdom', 'Writing simple spells, brewing bubbly potions… yesterday was just another day in your life as the Wizard’s Apprentice. Today, a horde of angry monsters is attacking The Counting Kingdom, and only you can defend the castle towers! Using your spells, potions, and the mighty power of mathematics, you must banish the monsters and save the kingdom!', '2014-08-04') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (21767, 'The Count Lucanor', null, null) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (18518, 'The Count', 'You are trapped in the castle of a Transylvanian count and only have three days and nights to defeat the count. In order to do so you have to solve puzzles, find secret passageways and most importantly have to be careful not to fall prey to the creatures of the night.', '1979-12-30') ON CONFLICT DO NOTHING;
