INSERT INTO games (id, name, summary, release) VALUES (14542, 'The Binding of Isaac: Wrath of the Lamb', 'The Binding of Isaac: Wrath of the Lamb is an official DLC expansion to The Binding of Isaac, which adds new Items, Rooms, Enemies, Bosses, and more.', '2012-05-27') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (7789, 'The Binding of Isaac: Rebirth', 'The Binding of Isaac: Rebirth is a top down, procedurally-generated rougelike game, remade based on the original game The Binding of Isaac. You play as Isaac, a little boy who is chased to the basement by his mother who intends to kill Isaac for her savior. You explore different levels, collect items and try to defeat your mother.. and whatever other evil awaits you. If you die, you restart with none of your items you collected, and you must explore the basement and beyond again.', '2014-11-03') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (26364, 'The Binding of Isaac: Afterbirth+', null, '2017-01-02') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (13177, 'The Binding of Isaac: Afterbirth', 'When Isaac’s mother starts hearing the voice of God demanding a sacrifice be made to prove her faith, Isaac escapes into the basement facing droves of deranged enemies, lost brothers and sisters, his fears, and eventually his mother.', '2015-10-29') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (2132, 'The Binding of Isaac', 'The Binding of Isaac is a randomly generated action RPG shooter with heavy Rogue-like elements. Following Isaac on his journey players will find bizarre treasures that change Isaac’s form giving him super human abilities and enabling him to fight off droves of mysterious creatures, discover secrets and fight his way to safety.', '2011-09-27') ON CONFLICT DO NOTHING;
