INSERT INTO games (id, name, summary, release) VALUES (16984, 'The Average Everyday Adventures of Samantha Browne', 'The Average Everyday Adventures of Samantha Browne is an interactive story about a painfully introverted college student who has to make oatmeal in the communal kitchen of her dorm. The gameplay will challenge you to find the right ways to help Samantha as she embarks on her journey to the other side of her bedroom door.', null) ON CONFLICT DO NOTHING;
