INSERT INTO games (id, name, summary, release) VALUES (8742, 'The Adventures of Gilligan''s Island', 'The Adventures of Gilligan''s Island is a game developed by Bandai for the Nintendo Entertainment System, based off of the famous 1960s sitcom "Gilligan''s Island". The player controls Skipper, who is followed around by the computer-controlled Gilligan.', '1990-06-30') ON CONFLICT DO NOTHING;
