INSERT INTO games (id, name, summary, release) VALUES (15925, 'That Dragon, Cancer', 'An immersive, narrative videogame that retells Joel Green’s 4-year fight against cancer through about two hours of poetic, imaginative gameplay that explores themes of faith, hope and love.', '2016-01-11') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (15903, 'Tharsis', 'Tharsis is a turn-based space strategy game. With dice. And cannibalism. It puts you in control of humanity’s first manned mission to Mars, just as it’s struck by a micrometeoroid storm. You must guide your crew through disasters, food shortages,  and the unforgiving nature of space — all while maintaining your sanity amidst cryptic signals and warnings from Mars.
