INSERT INTO games (id, name, summary, release) VALUES (19747, 'Teenage Mutant Ninja Turtles', 'Teenage Mutant Ninja Turtles (2003) is a beat ''em up game based on the 2003 Teenage Mutant Ninja Turtles television series, following the events of a handful of season one episodes.', '2003-10-20') ON CONFLICT DO NOTHING;
