INSERT INTO games (id, name, summary, release) VALUES (11500, 'Table Tennis', 'Table Tennis is the first of the 12 original games that was shipped with the Magnavox Odyssey system. It runs on Cartridge No.1.', '1972-12-30') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (12876, 'TY the Tasmanian Tiger 4', 'The hero loved by millions worldwide, TY the Tasmanian Tiger™, is back! Join TY on an exciting new adventure in the great Aussie Outback. Use your wits and boomerangs to find hidden treasures, help the colorful locals, and uncover the mysteries of the land Down Under.', '2015-09-16') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (24434, 'TX-1', null, '1983-12-30') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (26754, 'TV Superstars', 'Cook, Model, and boogie your way past 15 minutes of fame and into celebrity stardom! Use the PlayStation Eye camera’s unique face-capture technology to place you, friends and family into the starring roles of five action-packed TV shows. Then use your PlayStation Move motion controller to physically compete and rise to the top of the “A list” to become a household name. Battle it out on the red carpet, pose for paparazzi photographs, and sign autographs to become the Star you’ve always wanted to be!', '2010-10-25') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (8675, 'TV Sports Hockey', null, null) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (8671, 'TV Sports Football', 'So real it … sweats! Strap on your helmet and get ready for a pro football simulation that looks exactly like a Sunday network broadcast.  TV Sports: Football features amazing graphics and animation; real arcade action on offense and defense; individual and team statistics; and a 28 team, 16 game schedule with post season playoffs!', '1988-12-30') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (8674, 'TV Sports Boxing', 'So real it . . . sweats! Stick in your gumshield and get ready for the fight of your life as TV Sports: Boxing brings you all the action of a live network broadcast.  Create your own boxer, give him the best equipment, trainer and manager that money can buy and take him to the top.  Play against the computer or a friend in your bid for the championship belt.  You’ll be stunned by Cinemaware’s truly fantastic graphics and bone-crushing sound effects.  TV Sports: Boxing – it’s a knockout!', '1991-12-30') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (8672, 'TV Sports Basketball', 'So real it . . . sweats! Lace up you hightops and get psyched for a fast breaking, in-your-face, full court, 5-on-5 pro basketball simulation that looks exactly like a network broadcast.  TV Sports: Basketball gives you total control over an entire squad, including player substitutions and strategic double teaming.  Your skills as player and coach will receive their ultimate test as sudden shifts in momentum and fatigue take their toll.  Go head-to-head against another human or face an awesome computer opponent.  You’ll marvel at the Cinemaware quality animation and graphics; real arcade action on offensive and defensive; individual and team statistics; even the post season playoffs!  TV Sports: Basketball is truly Phi Slamma Jamma of basketball simulations!', '1990-12-30') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (8673, 'TV Sports Baseball', 'Batter Up! Pull out your old Louisville Slugger and get ready for real arcade action on offense and defense!', '1992-12-30') ON CONFLICT DO NOTHING;
