INSERT INTO games (id, name, summary, release) VALUES (20764, 'Syphon Filter: Dark Mirror', 'Dark Mirror  is the sequel to Syphon Filter: The Omega Strain. In Dark Mirror, a mysterious operation at an Alaskan oil reserve has Gabe Logan investigating the cause. He is quickly presented with a deeper operation that has him venturing across the world.', '2006-03-13') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (4117, 'Syphon Filter 3', 'Syphon Filter 3 is a stealth third-person shooter video game for the PlayStation. It was released in 2001, and is a sequel to Syphon Filter 2. Syphon Filter 3 holds the distinction of being the last first party game released for the PlayStation.', '2001-11-04') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (4116, 'Syphon Filter 2', 'Syphon Filter 2 is a stealth third-person shooter video game for the PlayStation. Released in 2000, its plot picks up immediately after where the previous Syphon Filter ended. It is the second title in the Syphon Filter series. Syphon Filter 2 was released on the PlayStation Store on September 10, 2009.', '2000-02-28') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (4115, 'Syphon Filter', 'Syphon Filter is a third-person shooter video game for the PlayStation released in 1999. It is the first game in the Syphon Filter series. It was re-released on December 4, 2006, on the PlayStation Network for use on the PSP. In March 2011, it was released for Android, exclusively for Sony''s XPERIA Play device.', '1999-08-11') ON CONFLICT DO NOTHING;
