INSERT INTO games (id, name, summary, release) VALUES (11424, 'Sym', 'Sym is a puzzle-platformer that explores social anxiety disorder. Play as Josh, a teenage boy trying to reconcile a maze of two contrasting worlds that coexist within the blank spaces of each other - his perception of reality, and the world he created to avoid his fears.', '2015-05-06') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (26779, 'Sylvio', 'Sylvio is a psychological horror game set in a tranquil, disturbing atmosphere. Help ghost recorder Juliette Waters face an evil curse and a wicked family cult with her microphone and the voices of the dead.', '2015-06-04') ON CONFLICT DO NOTHING;
