INSERT INTO games (id, name, summary, release) VALUES (19347, 'Sunken', 'Action RPG meets Roguelike in this atmospheric dungeon crawler. Spawned by two hardcore gamers, Sunken will constantly test your ability to adapt to a situation while it takes you through a dark journey of the one that has sinned.', '2016-05-25') ON CONFLICT DO NOTHING;
