INSERT INTO games (id, name, summary, release) VALUES (20178, 'Streamline', 'Streamline is the first in a new genre of Stream-first Games, built specifically for broadcasting! Everyone has a role in this fast-paced, arena-based game: Broadcasters easily make parties to battle with Players in multiple game-modes while Viewers control the game in real-time using a unique UI.', '2016-09-29') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (26025, 'Stray Souls: Dollhouse Story', 'Welcome to a town where something that should not exist runs free; where no one is safe, not even an innocent child; and where a desperate wife will risk her beating heart to find her husband!', '2011-02-28') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (21641, 'Stray Cat Crossing', 'Stray Cat Crossing is an indie pixel horror game about an encounter with a mysterious little girl named Cat. Explore a surreal world filled with colorful characters, challenging riddles, and dark secrets.', '2015-08-24') ON CONFLICT DO NOTHING;
