INSERT INTO games (id, name, summary, release) VALUES (23510, 'Strange Adventures in Infinite Space', 'Strange Adventures In Infinite Space is a hybrid space roguelike computer game. In the game players explore a "plausibly implausible" (fictional) region of the Milky Way galaxy called The Purple Void.', '2002-03-14') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (8751, 'Stranded Deep', 'Stranded Deep will feature a unique physically-based crafting and building system. Great care has been taken to minimize, even eliminate, most of the in-game UI. You will be able to craft a variety of crude weapons and tools such as spears, axes, knives, bows and even crossbows. And then there’s an assortment of everyday items to figure out how to craft. You will also be able to build a diverse range of unique shelters and rafts.', null) ON CONFLICT DO NOTHING;
