INSERT INTO games (id, name, summary, release) VALUES (9062, 'Strafe', 'Strafe is a singleplayer 3D action experience where the player can pick up a gun and shoot hordes of things in the face.  The developers have created technology that changes the levels every time you play for endless replayability. There are billions of experiences to be had with crazy secrets to find! We give you the levels, you paint them red.', '2017-03-27') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (19108, 'Story of a Cube', 'Story of a Cube is an action packed shooter about a lone cube and his quest for revenge. Mixing elements of twin-stick shooters and bullet hell games, this is a hardcore arcade game with a crazy amount of explosions.', '2016-03-03') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (15910, 'Story of Seasons: Trio of Towns', 'The series is celebrating its 20th anniversary with a new game for the Nintendo 3DS. The title of the game is Bokujō Monogatari: Mitsu no Sato no Taisetsu na Tomodachi (Harvest Moon/Story of Seasons: Precious Friends From Three Villages).INSERT INTO games (id, name, summary, release) VALUES (8608, 'Story of Seasons', null, '2014-02-26') ON CONFLICT DO NOTHING;
