INSERT INTO games (id, name, summary, release) VALUES (9516, 'Steal My Artificial Heart', 'On an android moon colony, a brutal murder of a metal madam seeds mystery in a cobalt-collar bar. Piece it together in this beautifully-illustrated neo-noir whodunit.', '2015-02-19') ON CONFLICT DO NOTHING;
