INSERT INTO games (id, name, summary, release) VALUES (26683, 'Statik', 'You’re trapped in a lab with strange experiments taking place, and you’ll solve increasingly trying puzzles in order to escape.', '2017-03-30') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (11531, 'States', 'States is one of the 12 original games that were shipped with the Magnavox Odyssey system. It runs on Cartridge No.6 and uses 50 state cards with a study map.', '1972-12-30') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (23628, 'State of Trove', null, null) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (20123, 'State of Mind', 'State of Mind is a futuristic thriller game delving into transhumanism. The game explores themes of separation, disjuncture and reunification, in a world that is torn between a dystopian material reality and a utopian virtual future.', '2017-03-30') ON CONFLICT DO NOTHING;
