INSERT INTO games (id, name, summary, release) VALUES (26201, 'Star Wars Battlefront: Rogue One: Scarif', 'Star Wars Battlefront Rogue One: Scarif is the fourth and final digital expansion pack as part of the Star Wars Battlefront Season Pass. &#13;
