INSERT INTO games (id, name, summary, release) VALUES (18783, 'Star Trek: The Motion Picture', null, '1982-12-30') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (26019, 'Star Trek: The Kobayashi Alternative', null, '1985-12-30') ON CONFLICT DO NOTHING;
