INSERT INTO games (id, name, summary, release) VALUES (9598, 'Spy Party', 'SpyParty is a tense competitive spy game set at a high society party. It''s about subtle behavior, perception, and deception, instead of guns, car chases, and explosions. One player is the Spy, trying to accomplish missions while blending into the crowd. The other player is the Sniper, who has one bullet with which to find and terminate the Spy!
