INSERT INTO games (id, name, summary, release) VALUES (9265, 'Spiral Knights', 'Spiral Knights is a cooperative adventure in a persistent world with a focus on fast-paced action. Arm yourself and join the ranks of the Spiral Knights; stranded on an alien world, they must explore the ever-changing Clockworks beneath its surface in hopes of reaching its mysterious Core.', '2011-04-03') ON CONFLICT DO NOTHING;
