INSERT INTO games (id, name, summary, release) VALUES (19506, 'Sonic Dash', 'Sonic Dash is an endless runner platformer video game developed by Hardlight and published by Sega. It was released exclusively on mobile phones, and is the second game in the Sonic the Hedgehog series developed by Hardlight for the platform, the first being the remake of Sonic Jump in 2012. The game was released on March 7, 2013, for iOS, November 26, 2013, for Android, and December 3, 2014, for Windows.', '2013-03-06') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (2267, 'Sonic Colors', 'Sonic Colors (ソニックカラーズ Sonikku Karāzu?), titled Sonic Colours in European and Australian markets, is a platforming game in the Sonic the Hedgehog series, developed by Sonic Team and published by Sega in 2010. The game was released worldwide for the Wii and Nintendo DS in November 2010.', '2010-11-10') ON CONFLICT DO NOTHING;
