INSERT INTO games (id, name, summary, release) VALUES (16634, 'Songs2See', 'Songs2See is a fun and easy way to learn a musical instrument. It is the first music game that lets you play along to well known songs using real acoustic instruments. The only required hardware is a simple PC microphone and your instrument.', '2013-10-01') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (25213, 'Songbringer', 'Songbringer is a sci-fi, pixel, action-adventure game in which one journeys through procedurally-generated, Zelda-inspired worlds. Along the way one will uncover powerful artifacts and forge them anew. Those skilled with the nanosword will vanquish giants and save a planet from being overrun.', '2017-12-30') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (23333, 'SongPop', 'SongPop 1 is a head-to-head music trivia game. The free App was released in May 2012 by FreshPlanet, a venture-backed, NYC–based startup. It is available for iPhone, iPad, Android, and Facebook. By 2015, the app had been downloaded 100 million times.
