INSERT INTO games (id, name, summary, release) VALUES (14315, 'Song of the Firefly', 'Song of the Firefly is a 3D exploration platformer. Explore the streets of post-apocalypse England as Sam, making use of gadgets left by Sarah and her firefly lantern to find your way around the world. With the lantern, you can make use of both light and dark to explore the world and use your gadgets in different ways. But be warned, total darkness will press in around you, and you may find yourself assaulted by shadows, which shun the light. 
INSERT INTO games (id, name, summary, release) VALUES (16998, 'Song of the Deep', 'A metroidvania-style action-adventure game following a young girl’s quest into the unknown to find her missing father.', '2016-07-11') ON CONFLICT DO NOTHING;
