INSERT INTO games (id, name, summary, release) VALUES (19220, 'Snake Pass', 'Welcome to Snake Pass; a physical action-puzzle game that sees the player slither, curl and climb their way through increasingly challenging worlds filled with evermore intricate obstacles and fiendishly mind-bending objectives!&#13;
