INSERT INTO games (id, name, summary, release) VALUES (26633, 'Sky Knights', 'Sky Knights is a top-down online action game for PC, in which you take on the role as a modern fighter pilot. You fly with and fight against other players online in a struggle to win each battle.', null) ON CONFLICT DO NOTHING;
