INSERT INTO games (id, name, summary, release) VALUES (22353, 'Skumlesens Skygge', 'Fourth game in a series of point-and-click games. Set in a factory of mister Shade.', '2000-12-30') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (26885, 'Skullstone', 'An ancient structure, long buried under Lisenian woods, is slowly rising due to unknown forces. Skullstone stands on top of it – a dreaded entrance into the darkness below. Evil creatures come out of it killing brave men, snatching frail women and helpless children. While local monks waste time praying in their monastery and deciding how to deal with the stream of evil beings, people of the town of Lisentar act on their own by calling for mercenaries who can save them.&#13;
INSERT INTO games (id, name, summary, release) VALUES (9023, 'Skulls of the Shogun', 'Skulls of the Shogun is a turn-based tactics video game developed by 17-BIT. The game is inspired by Advance Wars and features turn-based combat between undead samurai.', '2013-01-29') ON CONFLICT DO NOTHING;
