INSERT INTO games (id, name, summary, release) VALUES (11675, 'Ski or Die', 'Ski or Die is a 1990 winter sports game by Electronic Arts for the Amiga, NES, MS-DOS and Commodore 64. It consisted of 5 minigames which could be played individually or in a set sequentially. Up to 6 players could compete against each other via hotseat in 4 of the minigames, and semi-hotseat in 1 of them (up to two players at a time).
