INSERT INTO games (id, name, summary, release) VALUES (10845, 'Silent Storm: Sentinels', 'The add-on Sentinels starts shortly after the WWII nightmare. Veterans from all countries, even former enemies, stand together and form the secret investigation and intervention group “Sentinels”, for one purpose only: To stop the evil secret society “Thor''s Hammer” they have faced in war.', '2004-04-23') ON CONFLICT DO NOTHING;
