INSERT INTO games (id, name, summary, release) VALUES (482, 'Silent Hill 3', 'Silent Hill 3 is a direct sequel to the first installment in the series. It follows a teenaged girl named Heather as she becomes caught in a conflict within Silent Hill''s cult and discovers her true origin.', '2003-05-22') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (22066, 'Silent Hill 2: Inner Fears', 'An extended version of Silent Hill 2 containing a short bonus scenario and other minor additions.', '2002-02-06') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (481, 'Silent Hill 2', 'The second game in the Silent Hill franchise. Silent Hill 2 is a 3rd person, survival horror game with a heavy emphasis on psychological storytelling.', '2001-09-23') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (480, 'Silent Hill', 'Silent Hill follows Harry Mason as he searches for his missing adopted daughter in the mysterious town of Silent Hill. Stumbling upon a cult conducting a ritual to revive a deity it worships, Harry discovers his daughter''s true origin.', '1999-01-30') ON CONFLICT DO NOTHING;
