INSERT INTO games (id, name, summary, release) VALUES (20017, 'Shred It!', 'It’s time to get back to paper nature! Shred It! is an innovative endless runner by Extra Mile Studios. Have fun snowboarding your way down a never-ending hill in a beautiful paper-craft world.', '2016-03-17') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (8265, 'Shox: Rally Reinvented', null, '2002-09-22') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (9405, 'Showtime!', 'Showtime! will allow you to control and build your very own movie studio from the ground up! As a newcomer to Tinseltown it will be your job to create movies your audiences want to see as well as develop new technology to stay relevant in the film industry.', null) ON CONFLICT DO NOTHING;
