INSERT INTO games (id, name, summary, release) VALUES (12204, 'Shower With Your Dad Simulator 2015: Do You Still Shower With Your Dad?', '"Shower With Your Dad Simulator 2015: Do You Still Shower With Your Dad?" is a fast paced shower-simulation where you shower with your 8-bit dad. It''s good, clean fun!', '2015-09-01') ON CONFLICT DO NOTHING;
