INSERT INTO games (id, name, summary, release) VALUES (18811, 'Shock Tactics', 'Shock Tactics is inspired by the space western style of Firefly and games like Jagged Alliance and X-COM. The game is set on an ancient alien homeworld, procedurally generated each time you start a new game. Shock Tactics will be released later this year on Steam for PC and be available as a digital download.&#13;
