INSERT INTO games (id, name, summary, release) VALUES (19131, 'Shin Megami Tensei: Devil Survivor Overclocked', 'Devil Survivor Overclocked is an enhanced port of Devil Survivor made exclusively for the Nintendo 3DS. The port features enhanced art, voice acting for almost every piece of dialogue, new demons and an extended story (8th Day).', '2011-08-22') ON CONFLICT DO NOTHING;
