INSERT INTO games (id, name, summary, release) VALUES (26737, 'Shift DX', 'Shift DX marks the return of the classic Shift series. The game will feature all of the levels contained in Shift 1 and 2, along with 100 all-new levels designed specially for this release.&#13;
