INSERT INTO games (id, name, summary, release) VALUES (6863, 'Sherlock Holmes versus Arsène Lupin', 'This new Sherlock Holmes game sends us to 19th Century London where Arséne Lupin, a young French burglar with an impressive track record, has just challenged Scotland Yard and the most famous of detectives - Sherlock Holmes.
INSERT INTO games (id, name, summary, release) VALUES (24680, 'Sherlock Holmes in "Another Bow"', null, '1985-12-30') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (16357, 'Sherlock Holmes and The Hound of The Baskervilles', 'Help Sherlock Holmes, the famed detective, investigate the legend of a supernatural hound on the fog-shrouded moorland in England of the 19th century. Immerse yourself into the dangerous world of diabolical curse and discover the truth of the monstrous creature.', '2012-02-29') ON CONFLICT DO NOTHING;
