INSERT INTO games (id, name, summary, release) VALUES (13101, 'Shatter', 'Shatter is a retro-inspired brick-breaking game that merges familiar action with unique twists and a modern crafted production approach.', '2009-07-22') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (10822, 'Sharpe Investigations: Death on the Seine', 'Journalist-turned-food critic Taryn Sharpe and her assistant/photographer George Haske are sent on assignment to France to cover the opening of Paris''s hottest new restaurant, Le Roi Soleil. When they get there, they discover the restaurant''s celebrity chef is dead and the circumstances are suspicious. Strangely, the police rule the death a suicide and refuse to investigate. The restaurant''s owner (an old friend of Taryn''s) is convinced his star chef was murdered and begs her and George to find the culprit.', null) ON CONFLICT DO NOTHING;
