INSERT INTO games (id, name, summary, release) VALUES (7617, 'Shadow Realms', 'Shadow Realms is a cancelled action role-playing video game previously under development by BioWare''s Austin studio.', '2015-12-30') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (7819, 'Shadow Puppeteer', 'Manipulate light and darkness in this co-op puzzle platformer. Shadow Puppeteer combines platforming with puzzle-solving in a play of light and shadow. Having a truly Nordic yet original art style, the game tells the story of a boy and his shadow. Shadow Puppeteer is a co-op experience that focuses on teamwork and seeing the world through different perspectives. The combination of the characters'' skills unite and complete them.', '2014-09-28') ON CONFLICT DO NOTHING;
