INSERT INTO games (id, name, summary, release) VALUES (22463, 'Sexy Beach 3 Plus', 'Sexy Beach 3 Plus adds two new characters and one secret character, and several new gameplay features. If the player has any current progress in Sexy Beach 3, it can be loaded onto the expansion.', '2006-12-14') ON CONFLICT DO NOTHING;
