INSERT INTO games (id, name, summary, release) VALUES (6066, 'Severed', 'Severed Is a first-person dungeon crawler for the PlayStation Vita that uses touch based combat mechanics. It has a loot and crafting system based on severing enemy body parts to create and upgrade armor.', '2016-04-25') ON CONFLICT DO NOTHING;
